import { ButtonPanelViewOptions, InfoPanelOptions, LayoutOptions } from '.';
import { Account } from '../..';
import { InteractiveMapOptions, MarkerStyle } from '../../maps/types';
import { Servers } from '../../types/request.interface';
/**
 * A data type that represents the configuration options for an interactive map application.
 */
export interface InteractiveMapAppOptions {
    /**
     * Aeris account to use when requesting map data and imagery.
     *
     * @type {Account}
     */
    account?: Account;
    /**
     * Base paths for the API servers to use with data and image requests.
     *
     * @type {Servers}
     */
    servers?: Servers;
    /**
     * Configuration for the internal interactive map.
     *
     * @type {InteractiveMapOptions}
     */
    map?: InteractiveMapOptions;
    /**
     * Theme mode.
     */
    mode?: 'light' | 'dark';
    /**
     * Configuration for the application panel components.
     *
     * @type {({
     * 		layers?: LayoutOptions & ButtonPanelViewOptions;
     * 		info?: LayoutOptions & {
     * 			marker: MarkerStyle;
     * 		};
     * 		timeline?: LayoutOptions;
     * 		search?: LayoutOptions;
     * 		legends?: LayoutOptions;
     * 	})}
     */
    panels?: {
        layers?: LayoutOptions & ButtonPanelViewOptions & {
            enabled?: boolean;
        };
        info?: LayoutOptions & InfoPanelOptions & {
            enabled?: boolean;
            marker: MarkerStyle;
        };
        timeline?: LayoutOptions & {
            enabled?: boolean;
        };
        search?: LayoutOptions & {
            enabled?: boolean;
        };
        legends?: LayoutOptions & {
            enabled?: boolean;
        };
    };
}
