import { Coordinate } from '../../../types';
import Feature from '../../../maps/geo/Feature';
import { MarkerStyle } from '../../../maps/types';
/**
 * A data type that represents the data associated with a route point.
 */
export interface RoutePointInfo {
    /**
     * Point identifier.
     */
    id: string;
    /**
     * Custom name to display for the point, such as a business or facility.
     */
    title?: string;
    /**
     * Custom place name for the point, such as city or address.
     */
    place?: string;
    description?: any;
    /**
     * Point coordinate.
     */
    loc: Coordinate;
    /**
     * Epoch timestamp specifying the expected time the asset will depart from or arrive at the
     * point, in milliseconds.
     */
    timestamp?: number;
    /**
     * Custom marker style to use when rendering the point on a map.
     */
    markerStyle?: MarkerStyle;
}
/**
 * A data type that represents the data associated with a route asset.
 */
export interface RouteAsset {
    /**
     * Asset or payload title.
     */
    title?: string;
    /**
     * Asset or payload description or other information.
     */
    description?: string;
    /**
     * URL path to an image to display alongside the asset in an info panel.
     */
    imageUrl?: string;
    /**
     * Current coordinate of the asset.
     */
    loc?: Coordinate;
    /**
     * Asset status information.
     */
    status?: {
        /**
         * Current status of the asset or trip.
         */
        label: string;
        /**
         * Status badge color string to use for the status in an info panel.
         */
        color: string;
    };
}
/**
 * A data type that represents the data for a route.
 */
export interface RouteData {
    /**
     * Information about the departure route point.
     */
    departure?: RoutePointInfo;
    /**
     * Information about the destination route point.
     */
    destination?: RoutePointInfo;
    /**
     * Information about the asset's current position along the route when available.
     */
    current?: RoutePointInfo;
    /**
     * Additional information about the asset or payload associated with the route when available.
     *
     * @see RouteAsset;
     */
    asset?: RouteAsset;
    /**
     * GeoJSON feature providing the route path for the map.
     */
    feature?: Feature;
    /**
     * Array of GeoJSON features providing a series of waypoints along the route to plot on the map.
     */
    waypoints?: Feature[];
    /**
     * Total travel time for the route, in seconds.
     */
    durationSeconds?: number;
    /**
     * Total travel distance for the route, in meters.
     */
    distanceMeters?: number;
}
/**
 * A data type that represents the configuration for a route.
 */
export interface RouteConfig {
    /**
     * Route identifier.
     */
    id?: string;
    /**
     * Route name.
     */
    title?: string;
    /**
     * Array of route waypoints as strings or `ICoordiate` objects that define the start, end and
     * intermediate locations for the route.
     *
     * This value is only used when using a third-party directions API and have configured the
     * required `service` options. Values can be coordinates or place names as strings
     * (`47.25288,-122.44429`, or `austin,tx`) or coordinate objects
     * (`{ lat: 47.25288, lon: -122.44429 }`.
     */
    points: Coordinate[] | string[];
    /**
     * Route data and information.
     *
     * @see RouteData
     */
    data?: RouteData;
}
