/**
 * A pixel point representation.
 *
 * @export
 * @interface Point
 */
export interface Point {
    /** X value of the pixel position */
    x: number;
    /** Y value of the pixel position */
    y: number;
}
/**
 * A width and height representation.
 *
 * @export
 * @interface Size
 */
export interface Size {
    /** Width value */
    width: number;
    /** Height value */
    height: number;
}
