/**
 * A data type that represents the configuration options for an API request.
 */
export declare type ApiConfig = {
    /**
     * The API base server path.
     *
     * @type {string}
     */
    server?: string;
    /**
     * API client configuration to use with the request.
     *
     * @type {{
     * 		id: string,
     * 		secret: string
     * 	}}
     */
    client?: {
        /**
         * Client access id.
         *
         * @type {string}
         */
        id: string;
        /**
         * Client access secret.
         *
         * @type {string}
         */
        secret: string;
    };
};
