import { Point } from '.';
import { AnchorType } from './layout.type';
/**
 * A data type that represents SVG shape configuration options.
 */
export interface SVGShapeOptions {
    position?: AnchorType | Point;
    translate?: Point;
    stroke?: {
        width?: number;
        color?: string;
        opacity?: number;
    };
    fill?: {
        color?: string;
        opacity?: number;
    };
    transform?: string;
    size?: number[];
}
/**
 * A data type that represents SVG rectangle configuration options.
 *
 * @type {{
 * 		margin?: number;
 * 		radius?: number;
 * }}
 */
export interface SVGRectOptions extends SVGShapeOptions {
    margin?: number;
    radius?: number;
}
/**
 * A data type that represents SVG circle configuration options.
 *
 * @type {{
 * 		radius?: number;
 * 		center?: Point;
 * }}
 */
export interface SVGCircleOptions extends SVGShapeOptions {
    radius?: number;
    center?: Point;
}
/**
 * A data type that represents SVG path configuration options.
 *
 * @type {{
 * 		viewBox?: string;
 * }}
 */
export interface SVGPathOptions extends SVGShapeOptions {
    viewBox?: string;
}
/**
 * A data type that represents SVG text configuration options.
 */
export interface SVGTextOptions {
    anchor?: string;
    position?: AnchorType | Point;
    translate?: Point;
    transform?: string;
    font?: string;
    size?: number;
    style?: string;
    color?: string;
    value?: string | number;
    padding?: number[];
    stroke?: {
        color?: string;
        width?: number;
    };
}
/**
 * A data type that represents SVG image configuration options.
 */
export interface SVGImageOptions {
    url?: string;
    position?: AnchorType | Point;
    translate?: Point;
    transform?: string;
    size?: number[];
}
